import React, { Fragment, useState, useRef} from 'react';
import {v4 as uuidv4} from "uuid";
import { ToDoList } from './components/ToDoList';

export function App() {

  const [listaToDo, setListaToDo] = useState([{id:uuidv4(), task:"tarea de prueba", status: false}]);
  const toDoTaskRef = useRef();

  const handleToDoAdd = () => {
    const task = toDoTaskRef.current.value;
    if(task ===''){ 
      return;
    }
    else{    
      setListaToDo((prevToDo) =>{ 
        return [...prevToDo, {id:uuidv4(), task, status:false}];

      });
      toDoTaskRef.current.value = null;
    }
  }

  return (
      <Fragment>
        <ToDoList listaToDo={listaToDo} />
        <input ref={toDoTaskRef} type="text" placeholder="nueva" />
        <button onClick={handleToDoAdd}>
          +
        </button>
        <button onClick={handleToDoremove}>
          -
        </button>
      </Fragment>
  )
}
